Get-Content "~\.chia\mainnet\log\debug.log" -Wait -Tail 10 | select-string 'plots were eligible|error|warning|finished.signage.point|updated.wallet.peak' | ForEach {write-host -f $(if ($_ -Match "[1-9][0-9]*\sproofs") {'green'} elseif($_ -Match "[1-9][0-9]*\splots\swere\seligible") {'DarkCyan'} elseif($_ -Match "Time:\s[5-9][0-9]*\.\d+") {'DarkRed'} elseif($_ -Match "\d+\splots\swere\seligible") {'white'} elseif($_ -Match "error") {'red'} elseif($_ -Match "warning") {'yellow'} else {'DarkGray'}) $_.line}